import React, { useState } from 'react';
// 1. import `ChakraProvider` component to have chakra work correctly
import { ChakraProvider } from "@chakra-ui/react"
import { SimpleGrid } from "@chakra-ui/react"

/***************************************************************/

import { UiPanel } from "./components/ui_panel"
import { CardImagePanel } from "./components/card_image_panel"

/***************************************************************/
function CardGenerator() {

    // P because useState retruns a pair with [0] is the variable and [1] the set function
    const cardStruct = {
        imageFileP: useState({
            name: "",
            content: ""
        }),
        cardNameP: useState("")
    }


    return (
        <ChakraProvider>
            <SimpleGrid columns={2} h="100vh" w="100%">
                <UiPanel card={cardStruct} />
                <CardImagePanel card={cardStruct}/>
            </SimpleGrid>
        </ChakraProvider>

    );
}

export { CardGenerator };
