import React, { useState } from 'react';
// 1. import `ChakraProvider` component to have chakra work correctly
import { ChakraProvider } from "@chakra-ui/react"
import { Image, SimpleGrid, Box } from "@chakra-ui/react"

import './CardGenerator.css';

import { ImageSelector } from "./components/image_selector"

/*********** Class **************/
function UiPanel(props) {
    return (
        <Box bg="red">
          <ImageSelector setImageFileFunction={(file) => props.setImageFileFunction(file)}/>
        </Box>
        );
}

function CardImagePanel(props) {

    const imageFile = props.imageFile;

    return (
        <Box bg="blue">
            <Image alt={imageFile.name} src={imageFile.content}></Image>
        </Box>
    );
}

function CardGenerator() {

    const [imageFile, setImageFile] = useState({
        name: "",
        content: ""
    });

    return (
        <ChakraProvider>
            <SimpleGrid columns={2} h="100vh" w="100%">
                <UiPanel setImageFileFunction={(file) => setImageFile(file)}/>
                <CardImagePanel imageFile={imageFile}/>
            </SimpleGrid>
        </ChakraProvider>

    );
}

export { CardGenerator };
