import React, { useState } from 'react';
import { Box, VStack, HStack } from "@chakra-ui/react"
/**************************************************** */

import { Text } from "../style_components/text"
//import { Textbox } from "../style_components/textbox"
import { ManaSymbol } from "./mana_symbol"
import { symbols } from "../ressources/symbols"

/***************************************************************/
export function ManaCost(props) {
    let [val, setVal] = useState(0)

    //let manaSymbols = []

    return (
        <VStack spacing={2}>
            <HStack spacing={5}>
                <Box as="button" fontSize={30} onClick={() => setVal(val +1)}><ManaSymbol clNumber={val} shadow={true}/></Box>
                <Box as="button" fontSize={30}><ManaSymbol symbol={symbols.White} shadow={true}/></Box>
                <Box as="button" fontSize={30}><ManaSymbol symbol={symbols.Blue} shadow={true}/></Box>
                <Box as="button" fontSize={30}><ManaSymbol symbol={symbols.Black} shadow={true}/></Box>
                <Box as="button" fontSize={30}><ManaSymbol symbol={symbols.Red} shadow={true}/></Box>
                <Box as="button" fontSize={30}><ManaSymbol symbol={symbols.Green} shadow={true}/></Box>
            </HStack>
            <Text />    
         </VStack>
    );
}
