import React, { useRef } from 'react';
import { exportComponentAsPNG } from 'react-component-export-image';
/***************************************************************/

import { ImageBox } from "./image_box"
import { Button } from "../style_components/button"
/***************************************************************/

export function SaveImageButton(props) {
      
    const cref = useRef();
    const fileName = "card.png"
    return (
        <React.Fragment>
        <ImageBox ref={cref}/>
        <Button w={134} colorScheme="blue" onClick={() => exportComponentAsPNG(cref, {fileName, html2CanvasOptions: {backgroundColor: "black"}})}>Download card</Button>
        </React.Fragment>
    );
}


