import React from 'react';
import { Box, VStack, HStack } from "@chakra-ui/react"
/**************************************************** */

import { Text } from "../style_components/text"
//import { Textbox } from "../style_components/textbox"
import { Button } from "../style_components/button"

/***************************************************************/
export function ManaCost(props) {
    let val = 0
    const val2 = "ms-" + val + " "
    const val3 = "ms " + val2 + "ms-cost ms-shadow"

    function increaseVal(val){
        val = val + 1
    }

    return (
        <VStack spacing={2}>
            <HStack spacing={5}>
                <Box as="button" fontSize={30} onClick={() => increaseVal({val})}><i class={val3}/></Box>
                <Box as="button" fontSize={30}><i class="ms ms-w ms-cost ms-shadow"/></Box>
                <Box as="button" fontSize={30}><i class="ms ms-b ms-cost ms-shadow"/></Box>
                <Box as="button" fontSize={30}><i class="ms ms-r ms-cost ms-shadow"/></Box>
                <Box as="button" fontSize={30}><i class="ms ms-g ms-cost ms-shadow"/></Box>
                <Box as="button" fontSize={30}><i class="ms ms-u ms-cost ms-shadow"/></Box>
            </HStack>
            <Text />    
         </VStack>
    );
}
