import React from 'react';
import {Box, useStyleConfig} from "@chakra-ui/react"
/***************************************************************/

import { ImageBox } from "./image_box"
/***************************************************************/

export function CardImagePanel(props) {

    const style = useStyleConfig("CardImagePanel")

    return (
        <Box __css={style} sx={{wordSpacing: "0.2em"}} bg="blue.800">
            <ImageBox    imageFile={props.imageFile} 
                         cardName={props.cardName} nameFontSize={props.nameFontSize}
                         types={props.types} 
                         superTypes={props.superTypes} 
                         subTypes={props.subTypes} typesFontSize={props.typesFontSize}
                         manaCost={props.manaCost}
                         colorlessManaAmount={props.colorlessManaAmount}
                         cardFrameColor={props.cardFrameColor} 
                         spellDescription={props.spellDescription} spellFontSize={props.spellFontSize}
                         flavorText={props.flavorText} flavorTextFontSize={props.flavorTextFontSize}
                         power={props.power} toughness={props.toughness} ptFontSize={props.ptFontSize}
                         loyalty={props.loyalty}
                         imageCentering={props.imageCentering}
                         ref={props.cardImageRef} 
                         {...props}/>
        </Box>
    );
}
