import React from 'react';

function getSymbolCode(symbol) {
    switch (symbol) {
        case "red": return ("ms-r ");
        case "green": return ("ms-g ");
        case "black": return ("ms-b ");
        case "white": return ("ms-w ");
        case "blue": return ("ms-u ");
        default : return ("");        
    }
}

export function ManaSymbol(props) {
    const symbol = getSymbolCode(props.symbol)

    let classValue = "ms " + symbol + "ms-cost "
    
    if (props.clNumber) {
        classValue = classValue + "ms-" + props.clNumber + " "
    }
    
    if (props.shadow) {
        classValue = classValue + "ms-shadow"
    }

    return (
        <i class={classValue}/>
    );
}
