import React from 'react';
import { Box, VStack } from "@chakra-ui/react"
/**************************************************** */

import { ImageSelector } from "./image_selector"
import { CardName } from "./card_name"


/***************************************************************/
export function UiPanel(props) {
    
    const card = props.card

    const cardName = card.cardNameP[0]
    const setCardName = card.cardNameP[1]

    return (
        <Box bg="red">
            <VStack spacing={2}>
                <CardName cardName={card.cardName} setCardName={(value) => card.setCardName(value)}/>
                <ImageSelector setImageFileFunction={(file) => card.setImageFileFunction(file)} selectedImageFileName={card.selectedImageFileName}/>
            </VStack>
        </Box>
    );
}
