import React from 'react';
import { Box, useControllableState } from "@chakra-ui/react"
/***************************************************************/

import { Button } from "../style_components/button"
/***************************************************************/

export function FontSizeControler() {
    const [value, setValue] = useControllableState({ defaultValue: 40 })
    return (
      <div>
        <Button ml="10px" onClick={() => setValue(value + 1)}>+</Button>
        <Box as="span" mx="12px">
          {value}
        </Box>
        <Button onClick={() => setValue(value - 1)}>-</Button>
      </div>
    )
  }
